import Layout from '../../../components/Layout'
import { API } from '../../../services/apiResource';
import apiService from '../../../services/apiServices'
import React, { useState, useEffect } from 'react'




export default function Index(props) {
    if (props.isLoading) {
        console.log(props.hotel);
        return (
            <Layout>
                <div>ahihi</div>
            </Layout>
        )
    }
    else {
        return (
            <Layout>
                <div>Loading...</div>
            </Layout>
        )
    }
}

export async function getStaticProps({ params }) {
    const hotel = await apiService.getAll(API.HOTELS.getAll + `/${params.id}`);
    const comments = await apiService.getAll(API.HOTELS.getAll + `/${params.id}` + API.COMMENTS.get);
    if (hotel && comments) {
        return {
            props: {
                hotel: hotel,
                comments: comments,
                isLoading: false
            }
        }
    } else{
        return {
            props:{
                isLoading: true
            }
        }
    }

}
export async function getStaticPaths() {
    return {
        paths: [
            { params: { id: "" } }
        ],
        fallback: true,
    };
}
