import apiService from '../services/apiServices'
import { API } from '../services/apiResource'
import Layout from '../components/Layout'
import 'antd/dist/antd.css';
import { List, Row, Col } from 'antd';
import { MessageOutlined, LikeOutlined, StarOutlined } from '@ant-design/icons';

function Index(hotels) {
    const IconText = ({ icon, text }) => (
        <span>
            {React.createElement(icon, { style: { marginRight: 8 } })}
            {text}
        </span>
    );
    return (
        <>
            <Layout>
                <Row justify="center">
                    <List
                        itemLayout="vertical"
                        pagination={{
                            onChange: page => {
                                console.log(page);
                            },
                            pageSize: 8,
                        }}
                        dataSource={hotels.hotels}
                        renderItem={renderItem}
                    >
                    </List>
                </Row>
            </Layout>
        </>
    )
}
function renderItem(item) {
    return (
        < Col >
            <List.Item
                key={item.name}
                actions={[
                    <IconText icon={StarOutlined} text={item.vote} key="list-vertical-star-o" />,
                    <IconText icon={LikeOutlined} text={item.like} key="list-vertical-like-o" />,
                    // <IconText icon={MessageOutlined} text="2" key="list-vertical-message" />,
                ]}
                extra={
                    <img
                        width={172}
                        alt="logo"
                        src={item.image}
                    />
                }
            >
                <List.Item.Meta
                    title={<a href='#'>{item.title}</a>}
                />
                {item.description}
            </List.Item>
        </Col >
    )
}
export async function getStaticProps() {
    const hotels = await apiService.getAll(API.HOTEL.getAll);
    return {
        props: {
            hotels: hotels
        }
    }
}

export default Index
