import Layout from '../../../components/Layout'
import { API } from '../../../services/apiResource';
import apiService from '../../../services/apiServices'
import React, { useState, useEffect } from 'react'

function RenderComments(prop) {
    const commentRender = prop.comments.map(comment => {
        return (
            <>
                <div key={comment.id}>
                    <div>Author: {comment.author}</div>
                    <div>Comment: {comment.comment}</div>
                    <div>Date: {comment.createAt}</div>
                </div>
            </>
        )

    })
    return commentRender;
}
function RenderHotel(prop) {
    console.log(prop);

    return (
        <>
            {/* <div key={prop.hotel.id}>
                <div> {prop.hotel.name} </div>
                <div>{prop.hotel.address}</div>
                <div>{prop.hotel.image}</div>
                <div>{prop.hotel.price}</div>
                <div>{prop.hotel.vote}</div>
                <div>{prop.hotel.like}</div>
            </div> */}
        </>
    )
}
export default function Index(props) {
    return (
        <Layout>
            <RenderHotel hotel={props.hotel} />
            <RenderComments comments={props.comments} />
        </Layout>
    )
}
export async function getStaticProps({ params }) {
    const comments = await apiService.getAll(API.HOTELS.getAll + `/${params.id}/` + API.COMMENTS.get);
    return {
        props: {
            comments: comments
        }
    }
}
export async function getStaticPaths() {
    const hotels = await apiService.getAll(API.HOTELS.getAll);
    const paths = hotels.map(x => ({
        params: { id: x.id },
    }));
    return {
        paths,
        fallback: false,
    };
}
