import { useRouter } from 'next/router'
import Layout from '../../components/Layout'
import { API } from '../../services/apiResource';
import { getStaticPaths } from 'next'


export default function Detail(props) {

    return (
        <Layout>
            <div>{props.hotel.name}</div>
            <div>{props.hotel.image}</div>
            <div>{props.hotel.description}</div>
            <div>{props.hotel.price}</div>
            <div>{props.hotel.vote}</div>
            <div>{props.hotel.like}</div>
            <div>{props.hotel.adress}</div>
            {renderComment(props.comments)}
        </Layout>
    )
}
function renderComment(comments) {
    const commentList = comments.map(comment => {
        return (
            <div>
                <div>Author: {comment.author}</div>
                <div>Author: {comment.comment}</div>
            </div>
        )
    })
    return commentList;
}
Detail.getInitialProps = async (params) => {
    const { id } = params.query;
    const hotel = await apiService.getAll(API.HOTELS.getAll + `/${id}`);
    const comments = await apiService.getAll(API.HOTEL.getAll + `/${id}/` + API.COMMENTS.get);
    return {
        props: {
            hotel: hotel,
            comments: comments
        }
    }
}
