import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { Row, Col, Button } from 'antd';
import { LikeOutlined, StarOutlined } from '@ant-design/icons';




function RenderHotel(props) {
    const { hotel } = props;
    const [loading, setLoading] = useState(false);
    function bookingButton() {
        console.log("bookin is triggered");
        setLoading(true);
    }
    return (
        <div key={hotel.id}>
            <Row justify="center">
                <Col span={8}>
                    <img src={hotel.image}></img>
                </Col>
                <Col span={8} offset={3} >
                    <h1> {hotel.name} </h1>
                    <h2>Price: {hotel.price}$</h2>
                    <h2>Adress: {hotel.address}</h2>
                    <h3><StarOutlined />{hotel.vote}</h3>
                    <h3><LikeOutlined />{hotel.like}</h3>
                    <Button type="primary" onClick={bookingButton} loading={loading}>
                        Book
                    </Button>
                </Col>
            </Row>
        </div>
    )
}

RenderHotel.propTypes = {
    hotel: PropTypes.object,
}
RenderHotel.defaultProps = {
    hotel: {}
}

export default RenderHotel
